with category_graph_query as(
with prod_total_sales as ( select sum(ol.ol_units) as total_prod, p.pro_id, p.cat_id from ord_lines as ol inner join products as p on p.pro_id = ol.pro_id group by p.pro_id, p.cat_id ) select pts.cat_id, c.cat_name, sum(pts.total_prod) as total_sold from prod_total_sales as pts inner join categories as c on pts.cat_id = c.cat_id group by pts.cat_id, c.cat_name order by total_sold desc
) SELECT #COLUMNS# FROM category_graph_query #WHERE#
